import com.eviware.soapui.support.GroovyUtils

//Step 1: Read/extract a BLZ code from a request

//Create the instance of GroovyUtils
def groovyUtils = new GroovyUtils(context)

//Read the request XML file
def requestMessage = mockRequest.requestContent
def requestXml = groovyUtils.getXmlHolder(requestMessage)

//Extracting a BLZ code from an XML file
def blz = requestXml.getNodeValue('//blz:blz')
log.info "BLZ is equal to $blz"

//Step 2: Based on BLZ code, give a response
return "Response $blz"
