con:configuration.scriptText:
import groovy.util.XmlSlurper
// Extract the webservice response
// Step 1 - Syntax - context.expand ( property expansion)
//def cdatasub=context.expand('${getAirportInformationByAirportCode#Response}')
//log.info "cdatasub=$cdatasub"

//Step 2 - Extract more data from the response. Copy the declare name space from the Xpathc expression.Put \ infront of all single quotes
def cdata=context.expand('${getAirportInformationByAirportCode#Response#declare namespace ns1=\'http://www.webserviceX.NET\'//ns1:getAirportInformationByAirportCodeResponse/ns1:getAirportInformationByAirportCodeResult/text()}')
log.info cdata

//Step 3 - convert data from text to XML using XML slurper 
XmlSlurper XS = new XmlSlurper()
def xmlData=XS.parseText(cdata)

// Step 4 - Read the Airport code from the response
String airportCode =  xmlData.Table[0].AirportCode
String airportName = xmlData.Table[0].CityOrAirportName

//Step 5 - Assert the value 
assert airportCode == 'WAW'
assert airportName == 'WARSAW OKECIE'
